define(['taoQtiItem/qtiItem/core/choices/Choice', 'taoQtiItem/qtiItem/mixin/ContainerInline'], function (Choice, Container) { 'use strict';

    Choice = Choice && Choice.hasOwnProperty('default') ? Choice['default'] : Choice;
    Container = Container && Container.hasOwnProperty('default') ? Container['default'] : Container;

    var Hottext = Choice.extend({
        qtiClass: 'hottext'
    });

    Container.augment(Hottext);

    return Hottext;

});
